  public static void main(String[] args) throws Exception {
    FileInputStream fis = new FileInputStream(args[0]);
    for (int i = 0,c; i < 100 && (c = fis.read()) != -1; i++)
      System.out.println ("#" + i + " - " + Integer.toHexString(c) + " - " +
(char) c);
    fis.close();
  }
}

