source_code.code:
public class ScrolledWithinScrolled {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		Shell shell = new Shell();
		shell.setSize(100,100);
		shell.setLayout(new FillLayout());
		ScrolledComposite composite = new ScrolledComposite(shell, SWT.V_SCROLL);
		composite.setSize(100, 100);
		Tree tree = new Tree(composite, SWT.V_SCROLL);
		tree.setSize(100,100);
		for (int i = 0 ; i < 20; i++) {
			TreeItem item = new TreeItem(tree, SWT.NONE);
			item.setText(i + "");
			for (int j = 0; j < 20; j++) {
				TreeItem item2 = new TreeItem(item, SWT.NONE);
				item.setText(i + "-" + j);
			}
		}
		shell.open();

		Display display = shell.getDisplay();

		while (!shell.isDisposed()) {

			if (!display.readAndDispatch())
				display.sleep();
		}
	}
}

public static void main(String[] args) {
		Shell shell = new Shell();
		shell.setSize(100,100);
		Composite composite = shell;
		Tree tree = new Tree(composite, SWT.V_SCROLL);
		tree.setSize(100,100);
		for (int i = 0 ; i < 20; i++) {
			TreeItem item = new TreeItem(tree, SWT.NONE);
			item.setText(i + "");
			for (int j = 0; j < 20; j++) {
				TreeItem item2 = new TreeItem(item, SWT.NONE);
				item2.setText(i + "-" + j);
			}
		}
		shell.open();
		Display display = shell.getDisplay();

		while (!shell.isDisposed()) {

			if (!display.readAndDispatch())
				display.sleep();
		}
	}

