            }
        });
        
        shell.setSize(image.getBounds().width, image.getBounds().height);
        region.dispose();
        shell.open ();
        while (!shell.isDisposed ()) {
            if (!display.readAndDispatch ())
                display.sleep ();
        }
        image.dispose ();
        display.dispose ();
    }
}
