			pst1.setString(16,getModifiedBy());
			
			
			if(getLeaveId()!=0){
				int update=pst.executeUpdate();	
				System.out.println("Attendance status after update= "+getAttendanceStatus());
			}
			else{
				int update=pst1.executeUpdate();
				System.out.println("Attendance status after update= "+getAttendanceStatus());
			}
			
			}catch(Exception e)
			{
				System.out.println("Absent ERROR :" + e);
				return false;
			}
			finally{
				
				connClose();
				
			}
			
			return true;
	}

