source_code.code:
public class Typing {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		new Typing().echo();

	}
	
	public void echo() {
		boolean eof = false;
		InputStreamReader reader = new InputStreamReader(System.in);
		while (!eof) {
			char[] cbuf = new char[1024];
			try {
				int i = reader.read(cbuf);
				eof = i < 0;
			} catch (IOException e) {
				e.printStackTrace();
			}
			System.out.print(new String(cbuf));
		}
	}

}

