source_code.code:
public static void main(String[] args) {
	final Display display = new Display();
	final Shell shell = new Shell(display);
	shell.setBounds(10, 10, 300, 350);
	final Table table = new Table(shell, SWT.CHECK);
	table.setBounds(10,10,250,150);
	final TableColumn column = new TableColumn(table, SWT.NONE);
	column.setWidth(100);
	new TableColumn(table, SWT.NONE).setWidth(10);
	table.setHeaderVisible(true);
	new TableItem(table, SWT.NONE).setText("item");
	final Listener listener = new Listener() {
		public void handleEvent(Event event) {
			if (event.detail == 0) event.width = 150;
		}
	};
	Button pack = new Button(shell, SWT.PUSH);
	pack.setBounds(10,200,250,30);
	pack.setText("Pack column 0");
	pack.addListener(SWT.Selection, new Listener() {
		public void handleEvent(Event event) {
			column.pack();
		}
	});
	Button addListener = new Button(shell, SWT.PUSH);
	addListener.setBounds(10,250,250,30);
	addListener.setText("Add MeasureItem listener");
	addListener.addListener(SWT.Selection, new Listener() {
		public void handleEvent(Event event) {
			table.addListener(SWT.MeasureItem, listener);
		}
	});
	Button removeListener = new Button(shell, SWT.PUSH);
	removeListener.setBounds(10,300,250,30);
	removeListener.setText("Remove MeasureItem listener");
	removeListener.addListener(SWT.Selection, new Listener() {
		public void handleEvent(Event event) {
			table.removeListener(SWT.MeasureItem, listener);
		}
	});
	shell.open();
	while (!shell.isDisposed()) {
		if (!display.readAndDispatch()) display.sleep();
	}
	display.dispose();
}

