source_code.code:
public static void main(String[] args) {
	final Display display = new Display();
	final Shell shell = new Shell(display);
	shell.setBounds(10,10,300,350);
	final Table table = new Table(shell, SWT.NONE);
	table.setBounds(10,10,250,150);
	new TableItem(table, SWT.NONE).setText("              ");
	table.addListener(SWT.EraseItem, new Listener() {
		public void handleEvent(Event event) {
			event.gc.setLineDash(new int[] {2,4});
		}
	});
	table.addListener(SWT.PaintItem, new Listener() {
		public void handleEvent(Event event) {
			event.gc.setForeground(display.getSystemColor(SWT.COLOR_RED));
			event.gc.drawLine(event.x, event.y, event.x + event.width, event.y + event.height);
		}
	});
	shell.open();
	while (!shell.isDisposed()) {
		if (!display.readAndDispatch()) display.sleep();
	}
	display.dispose();
}

