source_code.code:
public abstract class Controller {

	/**
	 * @category internal
	 */
	private int value;
	
	/**
	 * @category API getter
	 */
	public int getValue() {
		return value;
	}
	
	/**
	 * @category setter
	 */
	public void setValue(int value) {
		this.value= value;
	}
	
}

