source_code.code:
public class WindowAndDialogTest {
	public static void main(String[] args) {
		final Display display = new Display();
		final Shell shell = new Shell(display);
		Composite basicInfoComposite = new Composite(shell, SWT.BORDER);
		GridLayout layout = new GridLayout();
		basicInfoComposite.setLayout(layout);
		GridData data = new GridData(GridData.FILL_BOTH);
		data.grabExcessHorizontalSpace = true;
		basicInfoComposite.setLayoutData(data);
		
		Button button = new Button(basicInfoComposite,SWT.PUSH);
		button.setText("Open Dailog");
		button.addSelectionListener(
			new SelectionListener(){
				public void widgetSelected(SelectionEvent e){
					openDialog(shell,display);
				}
				public void widgetDefaultSelected(SelectionEvent e){}
		
				});
		
		
		shell.setBounds(0,0,200,200);
		basicInfoComposite.setVisible(true);
		basicInfoComposite.pack();
		shell.open();
		while (!shell.isDisposed()) {
			if (!display.readAndDispatch())
				display.sleep();
		}
		display.dispose();
	}
	
	
	/**
	 * Open the text window for a editing text
	 */
	
	public static void openDialog(Shell parent, Display display){
		
		Shell shell = new Shell(parent);
		Composite basicInfoComposite = new Composite(shell, SWT.BORDER);
		GridLayout layout = new GridLayout();
		basicInfoComposite.setLayout(layout);
		GridData data = new GridData(GridData.FILL_BOTH);
		data.grabExcessHorizontalSpace = true;
		basicInfoComposite.setLayoutData(data);
		
		// path value label
		Label label = new Label(basicInfoComposite, SWT.WRAP );
		label.setText("My label");
		basicInfoComposite.setVisible(true);
		basicInfoComposite.pack();
		
		basicInfoComposite.setBounds(0,0,200,200);
		shell.open();
		long startTime = System.currentTimeMillis();
		long endTime = System.currentTimeMillis();
		while (!shell.isDisposed()) {
			if (!display.readAndDispatch())
				display.sleep();
		}	
	}
}

