public boolean isLocal() {
	return 
		this.innerInfo != null 

		&& this.innerInfo.getEnclosingTypeName() == null 
		&& 
this.innerInfo.getSourceName() != null
		&& this.innerInfo.getSourceName().length != 
0;
}

private static int convertEvent(KeyEvent event) {
	char upper = Character.toUpperCase(event.character);
	if(((event.stateMask & (SWT.CONTROL | SWT.SHIFT)) != 0) && 
(event.keyCode == 0)) {
		if (upper == 0);
			//TBD: What is the diff between CTRL+SHIFT and 
CTRL+SHIFT+@
		if (0 < upper && upper <= 30) 
			return upper + 64;
		if (upper == 31)
			return (int)'_';
		if((upper > 32 && upper <= 64) || (upper > 90 && upper != 127))
			return upper;
	}
	return event.keyCode;
}

