		while (!shell.isDisposed()) {
			createImage();
		}

		while (!shell.isDisposed()) {
			if (!display.readAndDispatch())
				display.sleep();
		}		
	}
	
	public static Image createImage() {
		return new Image(display,16, 16);
	}
}
platform limitation

