source_code.code:
public static void main(String[] args) {
	Display  display = new Display ();
	final Shell shell = new Shell (display);
	
	final Group group = new Group (shell, SWT.BORDER);
	group.setBounds (30, 30, 200, 200);
	final CCombo ccombo = new CCombo (group, SWT.BORDER);
	ccombo.setItems(new String[] {"a","b","c","d","e",});
	ccombo.setBounds (30, 30, 60, 28);
	
	shell.addListener (SWT.MouseDown, new Listener() {
		public void handleEvent(Event event) {
			Shell dialog = new Shell (shell, SWT.DIALOG_TRIM);
			group.setParent (dialog);
			group.setBounds (30, 30, 200, 200);
			dialog.open();
		}
	});
	shell.open ();
	while (!shell.isDisposed()) {
		if (!display.readAndDispatch()) {
			display.sleep ();
		}
	}
	display.dispose ();
}

public void initialize(Object owner) {

        parentShell = (owner == null) ? null : ((Control)owner).getShell();
        if (parentShell != null) {
            contentPanel = new Composite(parentShell, SWT.NONE);
            contentPanel.setLayout(new FillLayout());
        }
    }

public Object getContainer() {
        return (dialogArea == null) ? contentPanel : dialogArea;
    }

public void show(Object relativeComponent) {

        this.relativeComponent = (Composite)relativeComponent;

        int style = getShellStyle();

        style = (isModal)     ? style | SWT.APPLICATION_MODAL : style - 
SWT.APPLICATION_MODAL;

        style = (isResizable) ? style | SWT.RESIZE : style - SWT.RESIZE;

        setShellStyle(style);

        open();
    }

protected Control createDialogArea(Composite parent) {

        Composite dialogArea = (Composite)super.createDialogArea(parent);
        if (contentPanel == null) {
            contentPanel = dialogArea;
        } else {
            contentPanel.setParent(dialogArea);
        }
        dialogArea.setLayout(new FillLayout(SWT.VERTICAL));
        contentPanel.setLayout(new GridLayout(1, false));
        contentPanel.setLayoutData(new GridData(GridData.FILL_BOTH));
        if (component != null) component.setParent(contentPanel);
        return dialogArea;
    }

public class ComboPbm {
    public static void main(String[] args) {
        Display  display = new Display ();
        final Shell shell = new Shell (display);

        final Group group = new Group (shell, SWT.BORDER);
        group.setBounds (30, 30, 200, 200);
        final CCombo ccombo = new CCombo (group, SWT.BORDER);
        ccombo.setItems(new String[] {"a","b","c","d","e",});
        ccombo.setBounds (30, 30, 60, 28);
        ccombo.select(2);

        shell.addListener (SWT.MouseDown, new Listener() {
            public void handleEvent(Event event) {
                Dialog dlg = new Dialog();
                dlg.initialize(shell);

                dlg.setComponent(group);
                dlg.show(shell);
        }
        });
        shell.open ();
        while (!shell.isDisposed()) {
            if (!display.readAndDispatch()) {
                display.sleep ();
            }
        }
        display.dispose ();
    }
}

void dropDown (boolean drop) {
    
        if (drop == isDropped ()) return;
        if (!drop) {
            popup.setVisible (false);
            text.setFocus();
            return;
        }
    
        int index = list.getSelectionIndex();
        if (index != -1) list.setTopIndex(index);
    
        Rectangle listRect = list.getBounds();
        Point point        = getParent().toDisplay (getLocation ());
        Point comboSize    = getSize();
        int width          = Math.max (comboSize.x, listRect.width + 2);

/*   
 * Starts here
 *
 */
        if (! parentChanged && !popup.getParent().equals(getShell())) {
    
            parentChanged = true;
            popup.setParent(getShell ());
        }
    
        if (parentChanged) {
    
            Point textPoint = text.toDisplay(text.getLocation());
            point = getShell().toControl(textPoint);
        }
/*
 *  Ends here
 */
        popup.setBounds (point.x, point.y + comboSize.y, width, 
                         listRect.height + 2);
    
        popup.setVisible (true);
        list.setFocus();
    }

