import org.eclipse.swt.SWT;
import org.eclipse.swt.layout.FillLayout;
import org.eclipse.swt.layout.GridData;
import org.eclipse.swt.layout.GridLayout;
import org.eclipse.swt.widgets.Composite;
import org.eclipse.swt.widgets.Control;
import org.eclipse.swt.widgets.Shell;

public class Dialog
extends org.eclipse.jface.dialogs.Dialog {

    private Shell parentShell   = null;

    private Composite contentPanel = null;
    private Control component = null;
    private Composite relativeComponent = null;

    private String  title       = "";
    private boolean isResizable = false;
    private boolean isModal     = true;

    public Dialog() {
        this(null);
    }

    public Dialog(Shell parentShell) {
        super(parentShell);
        initialize(parentShell);
    }

    public void initialize(Object owner) {

        parentShell = (owner == null) ? null : ((Control)owner).getShell();
        if (parentShell != null) {
            contentPanel = new Composite(parentShell, SWT.NONE);
            contentPanel.setLayout(new FillLayout());
        }
    }

    public void setComponent(Object component) {
        this.component = (Composite)component;
    }

    public Object getComponent() {
        return component;
    }

    public Object getContainer() {
        return (dialogArea == null) ? contentPanel : dialogArea;
    }

    public void show() {
        show(null);
    }

    public void show(Object relativeComponent) {

        this.relativeComponent = (Composite)relativeComponent;

        int style = getShellStyle();

        style = (isModal)     ? style | SWT.APPLICATION_MODAL : style - 
SWT.APPLICATION_MODAL;

        style = (isResizable) ? style | SWT.RESIZE : style - SWT.RESIZE;

        setShellStyle(style);

        open();
    }

    public void create() {
        super.create();
        getShell().setText(title);
    }

    protected Control createDialogArea(Composite parent) {

        Composite dialogArea = (Composite)super.createDialogArea(parent);
        if (contentPanel == null) {
            contentPanel = dialogArea;
        } else {
            contentPanel.setParent(dialogArea);
        }
        dialogArea.setLayout(new FillLayout(SWT.VERTICAL));
        contentPanel.setLayout(new GridLayout(1, false));
        contentPanel.setLayoutData(new GridData(GridData.FILL_BOTH));
        if (component != null) component.setParent(contentPanel);
        return dialogArea;
    }
}











