source_code.code:
class B extends A { // use supertype 'A' for 'B' where possible
	public void beeOnly() {}
}

class User {
	void use(A a) {
		if (a instanceof B) { // 'B' wrongly replaced by 'A'
			B b= (B) a;
			b.beeOnly();
		}
	}
}

