public class FirstLoad {
    static public Hashtable hashTable = null;

    static public void insertedCode() {
        Plugin plugin = new Plugin();
        plugin.startup();
    }

    static {
        insertedCode(); // references hashTable
        hashTable = new Hashtable();
    }
}

public class Plugin {
    public void startup() {
        FirstLoad.hashTable.put("foo","bar");
        System.out.println(">> Plugin Started");
    }
}

