/**
	 * @param configuration
	 * @return
	 */
	private boolean hasClasspathChanged(ILaunchConfiguration configuration) {
		try {
			IRuntimeClasspathEntry[] entries=
JavaRuntime.computeUnresolvedRuntimeClasspath(configuration);
