source_code.code:
private PluginConverter acquirePluginConverter() throws Exception {
		ServiceTracker tracker = new ServiceTracker(
                     PDECore.getDefault()
				.getBundleContext(), 
                                PluginConverter.class.getName(), null);
		tracker.open();
		return (PluginConverter) tracker.getService();
	}

