source_code.code:
public class FooProducer
{
  public static final int N_METHODS = 50;
  public static final int N_STATEMENTS = Short.MAX_VALUE/(2 * N_METHODS);
  public static void main(String[] args) {
  	System.out.println("public class Foo {");
  	System.out.println("static java.util.Set hs = new java.util.HashSet
();");
  	for (int i = 0; i < N_METHODS; i++) {		
  	  System.out.println("public void test" + i + "() { ");
            for (int j = 0; j < N_STATEMENTS; j++) {
    	        System.out.println("hs.add(new Object());");
            }
          System.out.println("}");
        }
    System.out.println("}");
  }
}

public static void main(String[] args) {
	Display display = Display.getDefault();
	Shell shell = new Shell(display);
	shell.setBounds(10, 10, 200, 200);
	Button b1 = new Button(shell, SWT.PUSH);
	b1.setBounds(10, 10, 50, 50);
	b1.setText("button 1");
	b1.setToolTipText("tooltip 1");
	Button b2 = new Button(shell, SWT.PUSH);
	b2.setBounds(100, 100, 50, 50);
	b2.setText("button 2");
	b2.setToolTipText("tooltip 2");
	b2.setEnabled(false);
	shell.open();
	while (!shell.isDisposed()) {
		if (!display.readAndDispatch()) display.sleep();
	}
	display.dispose();
}

public static void Main() {
	Form f = new Form();
	Button b = new Button();
	b.Text = "test";
	b.Enabled = false;
	ToolTip t = new ToolTip();
	t.SetToolTip(b, "tooltip");
	t.ShowAlways = true;
	b.SetBounds(10, 10, 40, 20);
	f.Controls.Add(b);
	f.Show();
	Application.Run();
	Console.ReadLine();
}

