public class A {
	AA aa;
	void foo() {    
		int size = (aa.bar()).size();
		System.out.println("Size=" + size);
	}
}

class AA {
	Vector bar() { return new Vector(1); };
}

public class A {
  AA aa;
  void foo() {    
    (aa.bar()).get(0);
    // comment
    if (true) {
      System.out.println("Hello: " + toString()); //$NON-NLS-1$
    }
  }
}

