display = Display.getCurrent();
else
display = shell.getDisplay();

while (shell != null && !shell.isDisposed()) {
try {
if (!display.readAndDispatch())
eventLoopIdle();

} catch (Throwable e) {
e.printStackTrace();
}
}
}

}
