source_code.code:
public class MatchingAnonymous {
    static class Foo {
	void foo() {
	    System.out.println("Foo.foo:");
	}
    }

    static Foo x = new Foo()
	{
	    void foo() {
		System.out.println("Anonymous.foo:");
	    }
	};
    
    static void m() {
	class Local {
	    void foo() {
		System.out.println("Local.foo:");
	    }
	}

	new Local().foo();
    }

    public static void main(String[] args) {
	new Foo().foo();
	x.foo();
	m();
    }
}

