public abstract class UncheckedConversionCrash { public abstract class MyIterator<T> implements Iterator<T> {}
public abstract class Base<T extends Iterator<?>> { protected abstract T start(); public abstract Iterable getIterable(); }
public abstract class Derived extends Base<MyIterator<Object>> {
public MyIterator<Object> start() { return getIterable().iterator(); } } }
