create Macro in B.hpp file:
#define REF_WRAP(e)\
 class A \
 { \
  public: \
    A () \
    { } \
   \
   A& foo2(e& v) { return *this; ) \
   \
 }
//after it declare class:
class B
{
  REF_WRAP(B);
  B();
  void foo();
};
