  {
      switch(msg)
      {
          case WM_CLOSE:
              DestroyWindow(hwnd);
          break;
          case WM_DESTROY:
              PostQuitMessage(0);
          break;
          default:
              return DefWindowProc(hwnd, msg, wParam, lParam);
      }
      return 0;
  }


  // dialog procedure
  BOOL CALLBACK DialogProc(HWND hWnd, UINT uMsg, WPARAM wParam, LPARAM lParam)
  {
      switch(uMsg)
      {
