import org.eclipse.jdt.core.IPackageFragmentRoot;
import org.eclipse.jdt.core.IType;
import org.eclipse.jdt.core.JavaCore;
import org.eclipse.jdt.core.JavaModelException;

public class ConstructorExistsBugTest extends TestCase {
    static final String ICU1_CONTENT = "package test; public class Test { "
        + " public int add(int a, int b) { return a + b;  } }";

    static final String ICU2_CONTENT = "package test; public class Add { "
            + " public Add(Test test, int a, int b) {  } }";

    IJavaProject project;

