//NLclass.h
class NLclass{
public:

	NLclass() {}
	~NLclass() {}
      int att1;
	int att2;
	void method1() {}
	void method2() {}
	void uniqueMethod() {}
};

//main.cpp
#include "NLclass.h"

int function1(){
	return 1;
}
int funxon2(){
	return 2;
}
int funNLfunction1(){
	return 3;
}
int funNLfunction2(){
	return 4;
}
void uniqueNLfunction3() {}

