public class ListTest {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		final Display display = new Display();
		Shell shell = new Shell(display);
		final Image fImage = new Image(display, 20, 20);
		List list = new List(shell, SWT.NONE);
		populateList(list);
		list.addSelectionListener(new SelectionAdapter() {

			public void widgetSelected(org.eclipse.swt.events.SelectionEvent e) {
				if (e.item == null)
					System.out.println("null");
				else
					System.out.println(e.item.toString());
			};

			public void widgetDefaultSelected(
					org.eclipse.swt.events.SelectionEvent e) {
				if (e.item == null)
					System.out.println("null");
				else
					System.out.println(e.item.toString());
			};
		});

		shell.setBounds(0, 0, 200, 200);
		list.setBounds(10, 10, 150, 150);

		shell.open();
		while (!shell.isDisposed())
			display.readAndDispatch();

	}

	private static void populateList(List list) {
		String[] result = new String[10];
		for (int i = 0; i < result.length; i++) {
			list.add("Element " + String.valueOf(i));

		}

	}

}

